mapper.insert:

  SELECT NVL(MAX(MEMSEQ),0)+1 memseq FROM MEMTB

 INSERT INTO 
 MEMTB(MEMID
      , MEMSEQ
      , MEMNAME
      , MEMTEL
      , MEMBIRTH
      , REGDATE
      , MEMEMAIL
      , MEMGENDER
      , PASSWD)
 VALUES(#{memid}
      , #{memseq}
      , #{memname}
      , #{memtel}
      , #{membirth}
      , SYSDATE
      , #{mememail}
      , #{memgender}
      , #{passwd})
